mapper.insert:
insert into post_meta (post_id, meta_key, meta_value
)
values (#{postId,jdbcType=BIGINT}, #{metaKey,jdbcType=VARCHAR}, #{metaValue,jdbcType=LONGVARCHAR})

insert into category (
category_name

    ,category_description


    ,parent_category

)
values (
#{categoryName,jdbcType=VARCHAR}

    , #{categoryDescription,jdbcType=VARCHAR}


    ,#{parentCategory, jdbcType="BIGINT"}

)

mapper.delete:
delete
from post
where post_id = #{postId,jdbcType=BIGINT};
delete
from post_meta
where post_id = #{postId,jdbcType=BIGINT};
delete
from post_category
where post_id = #{postId,jdbcType=BIGINT};
delete
from post_tag
where #{postId,jdbcType=BIGINT};

mapper.select:
select category.category_id, category.category_name, category.category_description, category.parent_category
from category
where category.category_id = #{categoryId,jdbcType=BIGINT}

select tag.tag_id, tag.tag_name, tag.tag_description
from tag
where tag.tag_id = #{tagId,jdbcType=BIGINT}

select tag.tag_id, tag.tag_name, tag.tag_description
from tag
where tag.tag_name = #{tagName,jdbcType=VARCHAR}
